void ComponentRosjoy_node::startAllTasks() {
	// start task joy_nodeActivity
	if(connections.joy_nodeActivity.scheduler != "DEFAULT") {
		ACE_Sched_Params joy_nodeActivity_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.joy_nodeActivity.scheduler == "FIFO") {
			joy_nodeActivity_SchedParams.policy(ACE_SCHED_FIFO);
			joy_nodeActivity_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.joy_nodeActivity.scheduler == "RR") {
			joy_nodeActivity_SchedParams.policy(ACE_SCHED_RR);
			joy_nodeActivity_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		joy_nodeActivity->start(joy_nodeActivity_SchedParams, connections.joy_nodeActivity.cpuAffinity);
	} else {
		joy_nodeActivity->start();
	}
}

